package lesson5;

public class MyClass
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		double x,y ,answer; //bylo int
		x = 70;
		y = 30;
		answer = x+ y;
		System.out.println("x+y= " + answer);
		answer = x - y;
		System.out.println("x-y= " + answer);
		answer = x * y;
		System.out.println("x*y= " + answer);
		answer = x / y;
		System.out.println("x/y= " + answer);
		answer = x % y; //modulo
		System.out.println("x%y= " + answer);
	}

}
