package lesson7;

public class MyClass
{
	/*
	 * if(booolean_expression)
	 * {
	 *  execute if the boolean expression is true
	 * }
	 */
	
	/*
	 * == is equal to
	 * != is not equal to
	 * > is greater than
	 * < is less than
	 * >= is greater than or equal to
	 * <= is less than or equal to
	 */
	
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int x=10;
		if(x != 20) //check equality ==
		{
			System.out.println("x jest = 10");
		}
		else
		{
			System.out.println("x nie jest = 10");
		}

	}

}
